package week1.day2;


public class SumOfDigits {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 0;
	    int num=3;
	    int sum=0;
       while (i<=num) {
    	   
    	  sum=sum+i;
    	  i++;
		
	}
       {
    	   System.out.println("Sum of numbers is = " +sum);
       }
	}
}
